import React from 'react';
import {
  FileUploadDropzone,
  FileUploadList,
  FileUploadRoot,
} from '../Components/File-Buttons'; // Adjust the path if necessary

const HomePage = () => {
  const [files, setFiles] = React.useState([]);

  const handleDrop = (acceptedFiles) => {
    setFiles((prevFiles) => [...prevFiles, ...acceptedFiles]);
  };

  return (
    <FileUploadRoot maxW="xl" alignItems="stretch" maxFiles={10}>
      <FileUploadDropzone
        label="Drag and drop here to upload"
        description=".png, .jpg up to 5MB"
        onDrop={handleDrop} // Pass the handleDrop function
      />
      <FileUploadList files={files} />
      <h1>Welcome to MedVisor</h1>
    </FileUploadRoot>
  );
};

export default HomePage;
