        },
        selectNextItem(ctx) {
          const value = ctx.collection.getNextValue(ctx.value[0])
          set.selectedItem(ctx, value)
        },
        selectFirstItem(ctx) {
          const value = ctx.collection.firstValue
          set.selectedItem(ctx, value)
        },
        selectLastItem(ctx) {
          const value = ctx.collection.lastValue
          set.selectedItem(ctx, value)
