import React, { useState } from 'react';

const HomePage = () => {
  const [files, setFiles] = useState([]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files).map((file) => ({
      file,
      preview: URL.createObjectURL(file),
    }));
    setFiles(selectedFiles);
  };

  return (
    <div style={{ backgroundColor: '#a8d5a2', padding: '20px', textAlign: 'center' }}>
      <h1 style={{ color: '#ffffff' }}>Welcome to Medvisor</h1> {/* Text color now white */}
      <input
        type="file"
        multiple
        accept=".png, .jpg, .jpeg"
        onChange={handleFileChange}
        style={{ margin: '20px 0' }}
      />
      <h2 style={{ color: '#ffffff' }}>Uploaded Files:</h2> {/* This oneâ€™s white too */}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {files.map((fileObj, index) => (
          <li key={index} style={{ marginBottom: '10px' }}>
            <p style={{ color: '#ffffff' }}>{fileObj.file.name}</p> {/* File names, white as well */}
            <img
              src={fileObj.preview}
              alt="Preview"
              style={{ width: '200px', height: 'auto', borderRadius: '5px' }}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HomePage;
