import { extendTheme } from '@chakra-ui/react';

const theme = extendTheme({
  // Custom Colors
  colors: {
    brand: {
      50: '#F0F8FF',
      100: '#BFE3F4',
      200: '#A3D5F0',
      300: '#7BB3E6',
      400: '#4285F4',
      500: '#0A74DA',
      600: '#005BB5',
      700: '#003E8C',
      800: '#002567',
      900: '#001142',
    },
  },

  // Custom Fonts
  fonts: {
    heading: 'Poppins, sans-serif',
    body: 'Roboto, sans-serif',
  },

  // Custom Breakpoints (Responsive design)
  breakpoints: {
    sm: '320px',
    md: '768px',
    lg: '1024px',
    xl: '1280px',
  },

  // Custom Component Styles
  components: {
    Button: {
      baseStyle: {
        fontWeight: 'bold',  // Making buttons bold by default
      },
      sizes: {
        lg: {
          height: '48px',
          fontSize: '16px',
        },
        md: {
          height: '40px',
          fontSize: '14px',
        },
      },
      variants: {
        solid: {
          bg: 'brand.500',
          color: 'white',
          _hover: {
            bg: 'brand.600',
          },
        },
        outline: {
          borderColor: 'brand.500',
          color: 'brand.500',
          _hover: {
            bg: 'brand.50',
          },
        },
      },
    },
    // Add more component customizations if needed
  },
});

export default theme;
